/* Obfuscated Security Layer - Additional Protection */
(function(){
    'use strict';
    
    // Simple protection functions
    var preventAction = function(e) {
        e.preventDefault();
        e.stopPropagation();
        return false;
    };
    
    var preventDevTools = function(e) {
        // F12
        if (e.keyCode === 123) {
            e.preventDefault();
            return false;
        }
        // Ctrl+Shift+I, Ctrl+Shift+C, Ctrl+Shift+K
        if (e.ctrlKey && e.shiftKey && (e.keyCode === 73 || e.keyCode === 67 || e.keyCode === 75)) {
            e.preventDefault();
            return false;
        }
        // Ctrl+U (view source)
        if (e.ctrlKey && e.keyCode === 85) {
            e.preventDefault();
            return false;
        }
    };

    // Apply protections
    document.addEventListener('contextmenu', preventAction);
    document.addEventListener('selectstart', preventAction);
    document.addEventListener('dragstart', preventAction);
    document.addEventListener('keydown', preventDevTools);

    // Anti-debugging with error handling (less aggressive)
    setInterval(function(){
        try {
            var start = performance.now();
            // debugger; // Commented out to prevent false positives
            var end = performance.now();
            if (end - start > 200) { // Increased threshold
                console.warn('Timing anomaly detected');
            }
        } catch(e) {
            // Silently handle errors
        }
    }, 5000); // Less frequent checks

    // Console warning
    var warningMsg = 'STOP! This is protected content.';
    console.log('%c' + warningMsg, 'color: red; font-size: 30px; font-weight: bold;');
    
})();
